using Microsoft.EntityFrameworkCore;

namespace ASP.NET_Core_MVC._CRUD_операции.Models
{
	public class FilmContext : DbContext
	{
		public DbSet<Film > Films { get; set; }
		public FilmContext(DbContextOptions<FilmContext> options)
		   : base(options)
		{
			if (Database.EnsureCreated())
			{
				Films.Add(new Film { Title = "Интерстеллар", Director = "Кристофер Нолан", Genre = "фантастика, драма, приключения", Year = 2014, PosterPath = "~/Image/Interstellar.jpg", Description = "Когда засуха, пыльные бури и вымирание растений приводят человечество к продовольственному кризису, коллектив исследователей и учёных отправляется сквозь червоточину (которая предположительно соединяет области пространства-времени через большое расстояние) в путешествие, чтобы превзойти прежние ограничения для космических путешествий человека и найти планету с подходящими для человечества условиями." });
				Films.Add(new Film { Title = "Властелин Колец: Братство Кольца", Director = "Питер Джексон", Genre = "драма, приключения, фэнтези", Year = 2001, PosterPath = "~/Image/The Lord of the Rings.jpg", Description = "Первая часть легендарной трилогии по романам Дж.Р.Р.Толкиена. Фродо Бэггинс отправляется в опасное путешествие, когда его дядя Бильбо решает уйти на покой и оставляет ему загадочное и манящее кольцо. Оно было выковано в недрах Ородруина злым чародеем Сауроном, чтобы обрести безграничную власть над миром. Теперь главная цель так называемого Братства кольца — уничтожить его любой ценой." });

				Films.Add(new Film { Title = "Безумный Макс: Дорога ярости", Director = "Джордж Миллер", Genre = "боевик, приключения, фантастика", Year = 2015, PosterPath = "~/Image/Mad Max Fury Road.jpg", Description = "Вскоре после отмщения за смерть жены и сына, Макс Рокатански покинул ряды «Основного силового патруля» и уехал в глушь, где скитается в одиночестве, пока мир медленно падает в последствии нефтяного кризиса и глобальной войны. Не имея ничего, кроме своей машины «Перехватчик», Максу предстоит научиться, как выжить в пост-апокалиптической пустоши и сражаться с жестокими, безжалостными воинами, которые населяют её. Преследуемый призраками беспокойного прошлого Макс уверен, что лучший способ выжить - скитаться в одиночестве. Несмотря на это, он присоединяется к бунтарям, бегущим через всю пустыню на боевой фуре, под предводительством отчаянной Фуриосы. Они сбежали из Цитадели, страдающей от тирании Несмертного Джо, и забрали у него кое-что очень ценное. Разъярённый диктатор бросает все свои силы в погоню за мятежниками, ступая на тропу войны - дорогу ярости." });
				Films.Add(new Film { Title = "Зеленая миля", Director = "Фрэнк Дарабонт", Genre = "драма, фэнтези, криминал", Year = 1999, PosterPath = "~/Image/The Green Mile.jpg", Description = "Пол Эджкомб — начальник блока смертников в тюрьме «Холодная гора», каждый из узников которого однажды проходит «зеленую милю» по пути к месту казни. Пол повидал много заключённых и надзирателей за время работы. Однако гигант Джон Коффи, обвинённый в страшном преступлении, стал одним из самых необычных обитателей блока." });

				Films.Add(new Film { Title = "Темный рыцарь", Director = "Кристофер Нолан", Genre = "фантастика, боевик, триллер, криминал, драма", Year = 2008, PosterPath = "~/Image/The Dark Knight.jpg", Description = "Бэтмен поднимает ставки в войне с криминалом. С помощью лейтенанта Джима Гордона и прокурора Харви Дента он намерен очистить улицы Готэма от преступности. Сотрудничество оказывается эффективным, но скоро они обнаружат себя посреди хаоса, развязанного восходящим криминальным гением, известным напуганным горожанам под именем Джокер." });
				Films.Add(new Film { Title = "Хатико: Самый верный друг", Director = "Лассе Халльстрём", Genre = "драма, семейный, биография", Year = 2008, PosterPath = "~/Image/Hachi A Dog's Tale.jpg", Description = "Однажды, возвращаясь с работы, профессор колледжа нашел на вокзале симпатичного щенка породы акита-ину. Профессор и Хатико стали верными друзьями. Каждый день пес провожал и встречал хозяина на вокзале." });

				Films.Add(new Film { Title = "Матрица", Director = "Лана Вачовски, Лилли Вачовски", Genre = "фантастика, боевик", Year = 1999, PosterPath = "~/Image/The Matrix.jpg", Description = "Жизнь Томаса Андерсона разделена на две части: днём он — самый обычный офисный работник, получающий нагоняи от начальства, а ночью превращается в хакера по имени Нео, и нет места в сети, куда он бы не смог проникнуть. Но однажды всё меняется. Томас узнаёт ужасающую правду о реальности." });
				Films.Add(new Film { Title = "Гарри Поттер и узник Азкабана", Director = "Альфонсо Куарон", Genre = "фэнтези, приключения, семейный", Year = 2004, PosterPath = "~/Image/Harry Potter and the Prisoner of Azkaban.jpg", Description = "В третьей части истории о юном волшебнике полюбившиеся всем герои — Гарри Поттер, Рон и Гермиона — возвращаются уже на третий курс школы чародейства и волшебства Хогвартс. На этот раз они должны раскрыть тайну узника, сбежавшего из зловещей тюрьмы Азкабан, чье пребывание на воле создает для Гарри смертельную опасность..." });

				Films.Add(new Film { Title = "Пираты Карибского моря: Проклятие Черной жемчужины", Director = "Гор Вербински", Genre = "фэнтези, боевик, приключения", Year = 2003, PosterPath = "~/Image/Pirates of the Caribbean The Curse of the Black Pearl.jpg", Description = "Жизнь харизматичного авантюриста, капитана Джека Воробья, полная увлекательных приключений, резко меняется, когда его заклятый враг капитан Барбосса похищает корабль Джека Черную Жемчужину, а затем нападает на Порт Ройал и крадет прекрасную дочь губернатора Элизабет Свонн.\r\n\r\nДруг детства Элизабет Уилл Тернер вместе с Джеком возглавляет спасательную экспедицию на самом быстром корабле Британии, чтобы вызволить девушку и заодно отобрать у злодея Черную Жемчужину. Вслед за этой парочкой отправляется амбициозный коммодор Норрингтон, который к тому же числится женихом Элизабет.\r\n\r\nОднако Уилл не знает, что над Барбоссой висит вечное проклятие, при лунном свете превращающее его с командой в живых скелетов. Проклятье будет снято лишь тогда, когда украденное золото Ацтеков будет возвращено пиратами на старое место." });
				Films.Add(new Film { Title = "Звёздные войны: Пробуждение силы", Director = "Джей Джей Абрамс", Genre = "фантастика, боевик, приключения", Year = 2015, PosterPath = "~/Image/Star Wars Episode VII - The Force Awakens.jpg", Description = "Через тридцать лет после гибели Дарта Вейдера и Императора галактика по-прежнему в опасности. Государственное образование Первый Орден во главе с таинственным верховным лидером Сноуком и его правой рукой Кайло Реном идёт по стопам Империи, пытаясь захватить всю власть. В это нелёгкое время судьба сводит юную девушку Рей и бывшего штурмовика Первого Ордена Финна с героями войны с Империей - Ханом Соло, Чубаккой и генералом Леей. Вместе они должны дать бой Первому Ордену, однако настаёт тот момент, когда становится очевидно, что лишь джедаи могут остановить Сноука и Кайло Рена." });

				SaveChanges();
			}
		}
	}
}
